<body>
    <!--  *ngIf="displayForm" -->
    <div class="box" >
    <form (ngSubmit)="submitEvent()" class='form'>
       
        <button class="createNewEvent" (click)="toggleDisplay()">New Event</button>
            <div class="fields">
                <label for="eventName">Event Name</label>
                <input type="text" id="eventName" name="EventName" [(ngModel)]="newEvent.eventName"/>
                <!-- <label for="eventDate">Date</label>
                <input type="date" id="eventDate" name="EventDate" [(ngModel)]="newEvent.eventDate"/> -->
                <label for="eventTime">Time</label>
                <input type="datetime-local" id="eventTime" name="EventTime" [(ngModel)]="newEvent.eventTime"/>
                <label for="eventLocation">Location</label>
                <input type="text" id="eventLocation" name="Location" [(ngModel)]="newEvent.eventLocation"/>
                <label for="eventVenue">Venue</label>
                <input type="text" id="eventVenue" name="Venue" [(ngModel)]="newEvent.eventVenue"/>
                <label for="eventDescription">Description</label>
                <input type="text" id="eventDescription" name="Description" [(ngModel)]="newEvent.eventDescription">
                <label for="price">Admission Price</label>
                <input type="number" id="price" name="Price" [(ngModel)]="newEvent.price">
                <label for="eventImage">Image Filepath</label>
                <input type="text" id="eventImage" name="Image" [(ngModel)]="newEvent.eventImage">
            </div>
        
        <button class="createNewEvent" type="submit">Add</button>
    </form>
</div>
   
</body>
